import React, { useEffect, useState } from "react";
import Keycloak from "keycloak-js";
import UserSearch from "./Components/UserSearch";
import CreateUser from "./Components/CreateUser";
import "bootstrap/dist/css/bootstrap.min.css";

const App: React.FC = () => {
  const [keycloak, setKeycloak] = useState<null | Keycloak.KeycloakInstance>(null);
  const [authenticated, setAuthenticated] = useState(false);

  useEffect(() => {
    const keycloak = Keycloak({
      url: "https://auth.cern.ch/auth",
      realm: "cern",
      clientId: "sso-example",
    });

    keycloak
      .init({ onLoad: "login-required", flow: "implicit" })
      .then((authenticated) => {
        setKeycloak(keycloak);
        setAuthenticated(authenticated);
      })
      .catch(() => {
        alert("failed to authenticate");
      });
  }, []);

  if (keycloak) {
    if (authenticated)
      return (
        <div className="container py-5" style={{ background: "#f5f8fa" }}>
          <h1 className="text-center mb-5 text-primary">
            User Device Management
          </h1>
          <div className="row">
            <div className="col-md-6">
              <UserSearch />
            </div>
            <div className="col-md-6">
              <CreateUser />
            </div>
          </div>
          {/* Logout button */}
          <button onClick={() => keycloak.logout()}>Log out</button>
        </div>
      );
    else return <div>Unable to authenticate</div>;
  }

  return <div>Initializing Keycloak...</div>;
};

export default App;
