// App.tsx
import React, { useState } from 'react';
import Keycloak from 'keycloak-js';
import 'bootstrap/dist/css/bootstrap.min.css';
import UserSearch from './Components/UserSearch';
import DeviceList from './Components/DeviceList';
import CreateUser from './Components/CreateUser';

const initOptions = {
  url: 'https://auth.cern.ch/auth',
  realm: 'cern',
  clientId: 'sso-example',
  redirectUri: 'http://localhost:3000',
};

const keycloak = Keycloak(initOptions);

const App: React.FC = () => {
  const [auth, setAuth] = useState(false);

  const login = () => {
    keycloak.init({ onLoad: 'login-required' }).then((authenticated) => {
      if (authenticated) {
        setAuth(authenticated);
      } else {
        setAuth(false);
      }
    });
  }

  return (
    <div className="container py-5" style={{ background: '#f5f8fa' }}>
      <h1 className="text-center mb-5 text-primary">User Device Management</h1>
      <div className="row">
        <div className="col-md-6">
          <UserSearch />
        </div>
        <div className="col-md-6">
          {auth ? (
            <CreateUser />
          ) : (
            <button onClick={login} style={{ float: 'right' }}>Login</button>
          )}
        </div>
      </div>
      {auth && (
        <button onClick={() => keycloak.logout()} style={{ float: 'right' }}>Logout</button>
      )}
    </div>
  );
}

export default App;
