// components/CreateUser.tsx
import React, { useState } from 'react';

const CreateUser: React.FC = () => {
  const [userName, setUserName] = useState('');
  const [deviceName, setDeviceName] = useState(''); // new state variable for deviceName

  const handleCreate = async () => { // make handleCreate an async function
    console.log(`Creating user: ${userName} with device: ${deviceName}`);

    // Make POST request to your backend
    const response = await fetch('http://localhost:7164/api/User/Add', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json'
      },
      body: JSON.stringify({ userName, deviceName }) // send userName and deviceName in request body
    });

    // Check response status
    if (response.ok) {
      console.log('User created successfully');
    } else {
      console.error('Failed to create user');
    }

    setUserName('');
    setDeviceName('');
  };

  return (
    <div className="card p-3">
      <h2 className="mb-3">Create User</h2>
      <div className="input-group">
        <input 
          type="text" 
          value={userName} 
          onChange={e => setUserName(e.target.value)} 
          className="form-control"
          placeholder="Create new user..."
        />
        <input 
          type="text" 
          value={deviceName} 
          onChange={e => setDeviceName(e.target.value)} 
          className="form-control mt-2"
          placeholder="Enter device name..."
        />
        <div className="input-group-append">
          <button onClick={handleCreate} className="btn btn-outline-primary">Create</button>
        </div>
      </div>
    </div>
  );
}

export default CreateUser;
