// App.tsx
import React, { useEffect, useState } from 'react';
import Keycloak from 'keycloak-js';
import 'bootstrap/dist/css/bootstrap.min.css';
import UserSearch from './Components/UserSearch';
import DeviceList from './Components/DeviceList';
import CreateUser from './Components/CreateUser';

const initOptions = {
  url: 'https://auth.cern.ch/auth',
  realm: 'cern',
  clientId: 'sso-example',
  onLoad: 'login-required'
};

const keycloak = Keycloak(initOptions);

const App: React.FC = () => {
  const [auth, setAuth] = useState(false);

  useEffect(() => {
    keycloak.init({ onLoad: initOptions.onLoad }).then((authenticated) => {
      if (authenticated) {
        setAuth(authenticated);
      } else {
        setAuth(false);
      }
    });
  }, []);

  if (auth) {
    return (
      <div className="container py-5" style={{ background: '#f5f8fa' }}>
        <h1 className="text-center mb-5 text-primary">User Device Management</h1>
        <div className="row">
          <div className="col-md-6">
            <UserSearch />
          </div>
          <div className="col-md-6">
            <CreateUser />
          </div>
        </div>
        <button onClick={() => keycloak.logout()}>Logout</button>
      </div>
    );
  }
  else {
    return <div>Authenticating...</div>;
  }
};

export default App;
